import React, { Component } from "react";
import "../../App.css";
import { Tabs, Tab } from "react-mdl";
class Demo extends Component {
  state = {
    activeTab: 0,
    arr1: [
      {
        image:
          "url('https://cdn.mos.cms.futurecdn.net/nunnWHQKZhj4W2wUV7eqp5.jpg')",

        title: "The Guitar company "
      },
      {
        image:
          "url('https://www.bluecross.org.uk/sites/default/files/assets/images/124044lpr.jpg')",
        title: "pussy"
      },
      {
        image:
          "url('https://cdn-images-1.medium.com/max/1600/1*gaN7h99jHpTAP1ba3AWKng.png')",
        title: "clone"
      },

      {
        image:
          "url('https://www.androidcentral.com/sites/androidcentral.com/files/styles/large/public/article_images/2018/02/hangouts-chat-logo.jpg?itok=zemiXOQ8')",
        title: "clone"
      },

      {
        image:
          "url('https://www.xmple.com/wallpaper/white-green-graph-paper-grid-1920x1080-c2-ffffff-008080-l2-5-85-a-0-f-20.svg')",
        title: "clone"
      },

      {
        image:
          "url('https://webthemez.com/wp-content/uploads/2017/12/Fine-Best-App-Landing-Page.jpg')",
        title: "clone"
      }
    ],

    arr2: [3, 4, 5, 6]
  };

  render() {
    return (
      <div className="demo-tabs">
        <Tabs
          activeTab={this.state.activeTab}
          onChange={tabId => this.setState({ activeTab: tabId })}
          ripple
        >
          <Tab>HTML/CSS</Tab>
          <Tab>JAVASCRIPT</Tab>
        </Tabs>
        <section>
          <div className="content">
            Content for the tab: {this.state.activeTab}
          </div>
        </section>

        <section>
          <div className="content">
            {!this.state.activeTab
              ? this.state.arr1.map(item => {
                  return (
                    <div className="child__content">
                      <div
                        className="child__left"
                        style={{
                          backgroundImage: item.image
                        }}
                      />{" "}
                      <div className="child__right"> </div>{" "}
                    </div>
                  );
                })
              : this.state.arr2.map(item => {
                  return (
                    <div className="child__content">
                      <div className="child__left" style={{}}>
                        {" "}
                        {}{" "}
                      </div>{" "}
                      <div className="child__right"> ammu </div>{" "}
                    </div>
                  );
                })}
          </div>
        </section>
      </div>
    );
  }
}

export default Demo;
